import React from "react";
import './visualizer.css';
import { BubbleSort } from './SortingAlgos/bubblesort.js';
import { SelectionSort } from './SortingAlgos/selectionsort.js';
import { InsertionSort } from './SortingAlgos/insertionsort.js';
import { MergeSort } from './SortingAlgos/mergesort.js';
import { QuickSort } from "./SortingAlgos/quicksort";

// const animationTime = 2.5;
// const SIZE = 125;
const color1 = `red`;
const color2 = `rgb(177, 177, 248)`;
const color3 = `rgb(149, 246, 108)`;
const color4 = `green`;
class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            arr: [],
            SIZE: 50,
            animationTime: 1,
        }
    }
    componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < SIZE; i++) {
            array.push(randomIntegers(10, 600));
        }
        this.setState({ arr: array });
        this.changetoColor3(this.state.arr, color2);
    }
    changetoColor3(array, color) {
        for (let i = 0; i < array.length; i++) {
            const e = document.getElementById(i).style;
            e.backgroundColor = color;
        }
    }
    BubbbleSort() {
        const animations = BubbleSort(this.state.arr);
        // console.log(animations);
        for (let i = 0; i < animations.length; i++) {
            let color = "";
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            if (i % 3 === 0 || i % 3 === 2) {
                if (i % 3 === 0) {
                    color = color1;
                }
                if (i % 3 === 2) {
                    color = color2;
                }
                setTimeout(() => {
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, animationTime * i);

            }
            else {

                setTimeout(() => {
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, animationTime * i);
            }
        }
        setTimeout(() => {
            this.changetoColor3(this.state.arr, color3);
        }, animations.length * (animationTime));
    }
    selectionSort() {
        const animations = SelectionSort(this.state.arr);
        let c1 = this.state.arr.length;
        let c2 = 1;
        // console.log(animations);
        let color = "";
        for (let i = 0; i < animations.length; i++) {
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            const colorChange = c2 !== c1 ? true : false;
            if (colorChange) {
                c2++;
                // console.log("true");
                // console.log(i);
                setTimeout(() => {
                    color = color1;
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, animationTime * i);
                i--;
            }
            else if (color === color1) {
                setTimeout(() => {
                    color = color2;
                    // if (i === animations.length - 1) {
                    //     console.log("entered color3");
                    //     color = color3;
                    //     setTimeout(() => {
                    //         this.changetoColor3(this.state.arr, color3);
                    //     }, 15);
                    // }
                    console.log("Entered 1");
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, (animationTime) * i);
            }
            else {
                c1--;
                c2 = 1;
                // console.log("false");
                // console.log(i);
                setTimeout(() => {
                    console.log("Entered 2");
                    // if (i === animations.length - 1) {
                    //     console.log("entered color3");
                    //     color = color3;
                    //     setTimeout(() => {
                    //         this.changetoColor3(this.state.arr, color3);
                    //     }, 15);
                    // }
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, (animationTime) * i);
            }
        }
        setTimeout(() => {
            this.changetoColor3(this.state.arr, color3);
            console.log("entered last entered !!! ");
        }, (animations.length) * 1);

    }
    debugger() {
        const array = this.state.arr;
        // console.log(array);
        for (let i = 0; i < array.length; i++) {
            console.log(document.getElementById(i).style.height);

        }
        console.log("end");
    }
    isSorted() {
        for (let i = 0; i < 100; i++) {
            this.resetArray();
            const sortedarray = this.state.arr.slice().sort((a, b) => a - b);
            // console.log(sortedarray);
            const myarray = BubbleSort(this.state.arr);
            console.log(this.isequal(sortedarray, myarray));
        }
    }
    isequal(arr1, arr2) {
        for (let i = 0; i < arr1.length; i++) {
            if (arr1[i] === arr2[i])
                return false;
        }
        return true;
    }
    insertionSort() {
        const animations = InsertionSort(this.state.arr);
        // console.log(animations);
        for (let i = 0; i < animations.length; i++) {
            const [bar1, bar2, optype] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            switch (optype) {
                case "key":
                    setTimeout(() => {
                        bar1style.backgroundColor = "purple";
                    }, animationTime * i);
                    break;
                case "colorchange":
                    setTimeout(() => {
                        bar1style.backgroundColor = color1;
                        bar2style.backgroundColor = color1;
                    }, animationTime * i);
                    break;
                case "swap":
                    setTimeout(() => {
                        let temp = bar1style.height;
                        bar1style.height = bar2style.height;
                        bar2style.height = temp;
                    }, animationTime * i);
                    break;
                case "colorrev":
                    setTimeout(() => {
                        bar1style.backgroundColor = color2;
                        bar2style.backgroundColor = color2;
                    }, animationTime * i);

                    break;
                case "keyrev":
                    setTimeout(() => {
                        bar1style.backgroundColor = color2;
                    }, animationTime * i);
                    break;
                default:
                    break;
            }
        }
    }
    mergeSort() {
        const animations = MergeSort(this.state.arr);
        for (let i = 0; i < animations.length; i++) {
            const isColorChange = i % 3 !== 2;
            if (isColorChange) {
                const [barOneIdx, barTwoIdx] = animations[i];
                const barOneStyle = document.getElementById(barOneIdx).style;
                const barTwoStyle = document.getElementById(barTwoIdx).style;
                const color = i % 3 === 0 ? color1 : color2;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i * animationTime);
            } else {
                setTimeout(() => {
                    const [barOneIdx, newHeight] = animations[i];
                    const barOneStyle = document.getElementById(barOneIdx).style;
                    barOneStyle.height = `${newHeight}px`;
                }, i * animationTime);
            }
        }
    }
    quicksort() {
        const animations = QuickSort(this.state.arr);
        // console.log(animations);
        for (let i = 0; i < animations.length; i++) {
            const [bar1, bar2, optype] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            if (optype === "pivot") {
                setTimeout(() => {
                    bar1style.backgroundColor = "yellow";
                }, animationTime * i);
            }
            else if (optype === "pivotrev") {
                setTimeout(() => {
                    bar1style.backgroundColor = color2;
                }, animationTime * i);
            }
            else if (optype === "colorchange1") {
                setTimeout(() => {
                    bar1style.backgroundColor = color1;
                    bar2style.backgroundColor = color1;
                }, animationTime * i);
            }
            else if (optype === "colorreverse") {
                setTimeout(() => {
                    bar1style.backgroundColor = color2;
                    bar1style.backgroundColor = color2;
                }, animationTime * i);
            }
            else {
                setTimeout(() => {
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, animationTime * i);
            }
        }
        setTimeout(() => {
            this.changetoColor3(this.state.arr, color3);
        }, animationTime * animations.length);
    }
    onSliderChange(e)
    {
        console.log("Entered on slider change");
        console.log(e.target.value);
    }
    render() {
        const { arr } = this.state;
        return (
            <>
                <button className="btn" onClick={() => {
                    this.resetArray();
                }}>Generate New Array</button>
                <button className="btn" onClick={() => {
                    this.mergeSort();
                }}>MergeSort</button>
                <button className="btn" onClick={() => {
                    this.BubbbleSort();
                }}>BubbbleSort</button>
                <button className="btn" onClick={() => {
                    this.selectionSort();
                }}>SelectionSort</button>
                <button className="btn" onClick={() => {
                    this.quicksort();
                }}>QuickSort</button>
                <button className="btn" onClick={() => {
                    this.insertionSort();
                }}>InsertionSort</button>
                <input className="slider" type="range" min={5} max={200} defaultvalue={50} step={1} onChange={(e)=>this.onSliderChange(e)} />
                <div className="array" style={{margin:"0 4em 0 4em"}}>
                    {arr.map((value, index) => (
                        <li id={index} style={{ listStyle: "none", height: `${value}px` }}></li>
                    ))}
                </div>
            </>
        );
    }
}
function randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
export default SortingVisualizer;
