export function SelectionSort(array){
    const animations=[];
    let min=0;
    for(let i=0;i<array.length;i++)
    {
        min=i;
        for(let j=i+1;j<array.length;j++){
            animations.push([min,j]);
            // animations.push([min,j]);
            if(array[j]<array[min])
            {
               min=j;
            }
        }
        animations.push([i,min]);
        let temp=array[i];
        array[i]=array[min];
        array[min]=temp;
    }
    return animations;
}
