import React from "react";
import './visualizer.css';
class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            arr: []
        }
    }
    componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < 80; i++) {
            array.push(randomIntegers(10, 600));
        }
        this.setState({ arr: array });
    }
    MergeSort() {
     const e1= document.getElementById(1);
     const e2=document.getElementById(4);
    //  let col =red;
     console.log(e1);  
     console.log(e2);  
     let timer=setTimeout(()=>{
        e1.style.backgroundColor="red";
        e2.style.backgroundColor="red";
        e1.style.height=e2.style.height;
        let temp=e1.style.height;
        e2.style.height=temp;
     },1500);
     clearTimeout(timer);
     let timer2=setTimeout(()=>{
         e1.style.backgroundColor="whitesmoke";
         e2.style.backgroundColor="whitesmoke";
        },1500);
        clearTimeout(timer2);
    
    }
    render() {
        const { arr } = this.state;
        return (
            <>
                <button className="btn" onClick={() => {
                    this.resetArray();
                }}>Generate New Array</button>
                <button className="btn" onClick={() => {
                    this.MergeSort();
                }}>MergeSort</button>
                <div className="array">
                    {arr.map((value, index) => (
                        <li id={index} style={{ listStyle: "none", height: `${value}px` }}></li>

                    ))}
                </div>


            </>
        );

    }


}
function randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
export default SortingVisualizer;
