import React from "react";

class SortingVisualizer extends React.Component{
    constructor() {
        super(this.props);
        this.state={
            arr:[]
        }
    }
    componentDidMount()
    {
        this.resetArray();
    }
    resetArray(){
        const array=[];
        for(let i=0;i<100;i++)
        {
            array.push(randomIntegers(10,600));
        }
        this.setState({arr:array});
    }
}

export default SortingVisualizer;
