export function BubbleSort(array) {
    const animation=[];
    let i=0,j=0;
    for(i=0;i<array.length;i++)
    {
        for(j=i+1;j<array.length-1-i;j++)
        {
            animation.push(j,j+1);
            animation.push(j,j+1);
            if(array[j]>array[j+1])
            {
                animation.push(j,j+1);
                let temp=array[j+1];
                array[j+1]=array[j];
                array[j]=temp;
            }
            else{
                animation.push(j,j);
            }
        }
    }

    return animations;
}
