import React from "react";

class SortingVisualizer extends React.Component {
    constructor() {
        super(this.props);
        this.state = {
            arr: []
        }
    }
    componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < 100; i++) {
            array.push(this.randomIntegers(10, 600));
        }
        console.log(array);
        this.setState({ array });
        // console.log(arr);
    }

    randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
      
}

export default SortingVisualizer;
