export function InsertionSort(array) {
    const animations=[];
    // console.log(array);
    for(let i=0;i<array.length;i++)
    {
        let key=array[i];
        let j= i-1;
        animations.push([i,i]);
        while(j>=0&& array[j]>key)
        {
            array[j+1]=array[j];
            animations.push([j,j+1]);
            animations.push([j,j+1]);
            animations.push([j,j+1]);
            j--;
        }
        animations.push([j+1,array.length]);
        array[j+1]=key;
    }
    console.log(array);
    // console.log(animations);
    return animations;
}
