import React from "react";
import './visualizer.css';
class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            arr: []
        }
    }
    componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < 80; i++) {
            array.push(randomIntegers(10, 600));
        }
        this.setState({ arr: array });
    }
    delay(i) {
        setTimeout(() => {
            console.log("Entered delay");
        }, 300 * i);
    }
    MergeSort() {
        const e1 = document.getElementById(1);
        const e2 = document.getElementById(4);
        //  let col =red;
        console.log(e1);
        console.log(e2);
        setTimeout(() => {
            e1.style.backgroundColor = "red";
            e2.style.backgroundColor = "red";
            let temp = e1.style.height;
            e1.style.height = e2.style.height;
            e2.style.height = temp;
        }, 300);

        setTimeout(() => {
            e1.style.backgroundColor = "rgb(177, 177, 248)";
            e2.style.backgroundColor = "rgb(177, 177, 248)";
        }, 1200);


    }
    BubbbleSort() {
        const { arr } = this.state;
        let Change = false;
        let tempid, tempheight;
        // let i=0,j=0;
        for ( let i = 0; i < arr.length; i++) {
            setTimeout(() => {

                for ( let j = 0; j < arr.length - 1 - i; j++) {
                    setTimeout(() => {
                        if (arr[j + 1] < arr[j]) {
                            let temp = arr[j + 1];
                            arr[j + 1] = arr[j];
                            arr[j] = temp;
                            Change = true;
                        }
                        else {
                            Change = false;
                        }
                        let e1 = document.getElementById(j).style;
                        let e2 = document.getElementById(j + 1).style;

                        // console.log("entered timeout.");
                        // setTimeout(() => {
                            e1.backgroundColor = "red";
                            e2.backgroundColor = "red";
                            if (Change) {
                                tempheight = e1.height;
                                e1.height = e2.height;
                                e2.height = tempheight;
                            }
                            e1.backgroundColor = `rgb(177, 177, 248)`;
                            e2.backgroundColor = `rgb(177, 177, 248)`;
                        // }, 100);


                    }, 150*j);
                    }
            
                
            }, 300*j);
            }

        // this.setState=({arr:arr});
    }
    render() {
        const { arr } = this.state;
        return (
            <>
                <button className="btn" onClick={() => {
                    this.resetArray();
                }}>Generate New Array</button>
                <button className="btn" onClick={() => {
                    this.MergeSort();
                }}>MergeSort</button>
                <button className="btn" onClick={() => {
                    this.BubbbleSort();
                }}>BubbbleSort</button>
                <div className="array">
                    {arr.map((value, index) => (
                        <li id={index} style={{ listStyle: "none", height: `${value}px` }}></li>

                    ))}
                </div>


            </>
        );

    }


}
function randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
export default SortingVisualizer;
