export function SelectionSort(array){
    const animations=[];
    let min=0;
    console.log("The array before sorting:");
    console.log(array);
    console.log("The array after sorting:");
    for(let i=0;i<array.length;i++)
    {
        min=i;
        for(let j=i+1;j<array.length;j++){
            if(array[j]<array[min])
            {
               min=j;
            }
        }
        let temp=array[i];
        array[i]=array[min];
        array[min]=temp;
    }
    console.log(array);
    return animations;
}
