import React from "react";
import './visualizer.css';
import { BubbleSort } from './SortingAlgos/bubblesort.js';
import { SelectionSort } from './SortingAlgos/selectionsort.js';
import { InsertionSort } from './SortingAlgos/insertionsort.js';

const animationTime = 300;
const color1 = `red`;
const color2 = `rgb(177, 177, 248)`;
const color3 = `rgb(149, 246, 108)`;
const color4 = `green`;
class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            arr: []
        }
    } componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < 10; i++) {
            array.push(randomIntegers(10, 600));
        }
        this.setState({ arr: array });
        this.changetoColor3(this.state.arr, color2)
    }
    delay(i) {
        setTimeout(() => {
            console.log("Entered delay");
        }, 300 * i);
    }
    changetoColor3(array, color) {
        for (let i = 0; i < array.length; i++) {
            const e = document.getElementById(i).style;
            e.backgroundColor = color;
        }
    }
    MergeSort() {
        const e1 = document.getElementById(1);
        const e2 = document.getElementById(4);
        console.log(e1);
        console.log(e2);
        setTimeout(() => {
            e1.style.backgroundColor = "red";
            e2.style.backgroundColor = "red";
            let temp = e1.style.height;
            e1.style.height = e2.style.height;
            e2.style.height = temp;
        }, 300);

        setTimeout(() => {
            e1.style.backgroundColor = "rgb(177, 177, 248)";
            e2.style.backgroundColor = "rgb(177, 177, 248)";
        }, 1200);


    }
    BubbbleSort() {
        const animations = BubbleSort(this.state.arr);
        console.log(animations);
        for (let i = 0; i < animations.length; i++) {
            let color = "";
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            if (i % 3 === 0 || i % 3 === 2) {
                if (i % 3 === 0) {
                    color = color1;
                }
                if (i % 3 === 2) {
                    color = color2;
                }
                setTimeout(() => {
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, animationTime * i);

            }
            else {

                setTimeout(() => {
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, animationTime * i);
            }
        }
        setTimeout(() => {
            this.changetoColor3(this.state.arr, color3);
        }, animations.length * (animationTime))
    }
    selectionSort() {
        const animations = SelectionSort(this.state.arr);
        let c1 = this.state.arr.length;
        let c2 = 1;
        // console.log(animations);
        let color = "";
        for (let i = 0; i < animations.length; i++) {
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            const colorChange = c2 !== c1 ? true : false;
            if (colorChange) {
                c2++;
                // console.log("true");
                // console.log(i);
                setTimeout(() => {
                    color = color1;
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, animationTime * i);
                i--;
            }
            else if (color === color1) {
                setTimeout(() => {
                    color = color2;
                    // if (i === animations.length - 1) {
                    //     console.log("entered color3");
                    //     color = color3;
                    //     setTimeout(() => {
                    //         this.changetoColor3(this.state.arr, color3);
                    //     }, 15);
                    // }
                    console.log("Entered 1");
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, (animationTime) * i);
            }
            else {
                c1--;
                c2 = 1;
                // console.log("false");
                // console.log(i);
                setTimeout(() => {
                    console.log("Entered 2");
                    // if (i === animations.length - 1) {
                    //     console.log("entered color3");
                    //     color = color3;
                    //     setTimeout(() => {
                    //         this.changetoColor3(this.state.arr, color3);
                    //     }, 15);
                    // }
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, (animationTime) * i);
            }
        }
        setTimeout(() => {
            this.changetoColor3(this.state.arr, color3);
            console.log("entered last entered !!! ");
        }, (animations.length) * 1);

    }
    insertionSort() {
        const animations = InsertionSort(this.state.arr);
        console.log(animations);
        let key, keyidx = 0;
        let bar2style = document.getElementById(0).style;
        let operation = 1;
        for (let i = 0; i < animations.length; i++) {
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            if (bar2 !== this.state.arr.length) {
                bar2style = document.getElementById(bar2).style;
            }
            // console.log(bar1style);
            // console.log(bar2style);
            if (bar1 === bar2) {
                // console.log("entered 1");
                // setTimeout(() => {
                bar1style.backgroundColor = "violet";
                key = bar1style.height;
                keyidx = bar1;
                // }, animationTime * i);
            }
            else if (bar2 === this.state.arr.length) {
                // console.log("entered 2");
                // setTimeout(() => {
                bar1style.height = key;
                document.getElementById(keyidx).style.backgroundColor = color2;
                // }, animationTime * i);
            }
            else {
                // console.log("entered 3");
                // setTimeout(() => {
                if (operation === 1) {
                    if (bar2 !== keyidx) {
                        bar1style.backgroundColor = color1;
                        bar2style.backgroundColor = color1;
                    }
                    operation = 2;
                }
                else if (operation === 2) {
                    if (bar2 !== keyidx) {
                        let temp = bar1style.height;
                        bar2style.height = temp;
                    }
                    operation = 3;
                }
                else if (operation === 3) {
                    bar1style.backgroundColor = color2;
                    bar2style.backgroundColor = color2;
                    operation = 1;
                }
                // }, animationTime * i);
            }
        }
    }
    // onSliderChange() {
    //     console.log(document.getElementsByClassName("slider"));
    // }
    render() {
        const { arr } = this.state;
        return (
            <>
                <button className="btn" onClick={() => {
                    this.resetArray();
                }}>Generate New Array</button>
                <button className="btn" onClick={() => {
                    this.MergeSort();
                }}>MergeSort</button>
                <button className="btn" onClick={() => {
                    this.BubbbleSort();
                }}>BubbbleSort</button>
                <button className="btn" onClick={() => {
                    this.selectionSort();
                }}>SelectionSort</button>
                <button className="btn" onClick={() => {
                    this.insertionSort();
                }}>InsertionSort</button>
                {/* <input className="slider" type="range" min={5} max={200} defaultvalue={50} step={1} onChange={this.onSliderChange()} /> */}
                <div className="array">
                    {arr.map((value, index) => (
                        <li id={index} style={{ listStyle: "none", height: `${value}px` }}></li>

                    ))}
                </div>


            </>
        );

    }


}
function randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
export default SortingVisualizer;
