import React from "react";
import './visualizer.css';
import { BubbleSort } from './SortingAlgos/bubblesort.js';

const animationTime = 1;
const color1 = `red`;
const color2 = `rgb(177, 177, 248)`;
const color3 = `rgb(149, 246, 108)`;
class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            arr: []
        }
    }
    componentDidMount() {
        this.resetArray();
    }
    resetArray() {
        const array = [];
        for (let i = 0; i < 100; i++) {
            array.push(randomIntegers(10, 600));
        }
        this.setState({ arr: array });
        for(let i=0;i<array.length;i++)
        {
            const e= document.getElementById(i).style;
            e.backgroundColor=color2;
        }
    }
    delay(i) {
        setTimeout(() => {
            console.log("Entered delay");
        }, 300 * i);
    }
    changetoColor3(array)
    {
        for(let i=0;i<array.length;i++)
        {
            const e= document.getElementById(i).style;
            e.backgroundColor=color3;
        }
    }
    MergeSort() {
        const e1 = document.getElementById(1);
        const e2 = document.getElementById(4);
        console.log(e1);
        console.log(e2);
        setTimeout(() => {
            e1.style.backgroundColor = "red";
            e2.style.backgroundColor = "red";
            let temp = e1.style.height;
            e1.style.height = e2.style.height;
            e2.style.height = temp;
        }, 300);

        setTimeout(() => {
            e1.style.backgroundColor = "rgb(177, 177, 248)";
            e2.style.backgroundColor = "rgb(177, 177, 248)";
        }, 1200);


    }
    BubbbleSort() {
        const animations = BubbleSort(this.state.arr);
        console.log(animations);
        for (let i = 0; i < animations.length; i++) {
            let color="";
            const [bar1, bar2] = animations[i];
            const bar1style = document.getElementById(bar1).style;
            const bar2style = document.getElementById(bar2).style;
            if (i % 3 === 0 || i % 3 === 2) {
                if (i % 3 === 0) {
                    color = color1;
                }
                if (i % 3 === 2) {
                    color = color2;
                }
                setTimeout(() => {
                    bar1style.backgroundColor = color;
                    bar2style.backgroundColor = color;
                }, animationTime * i);

            }
            else {
                setTimeout(() => {
                    let temp = bar1style.height;
                    bar1style.height = bar2style.height;
                    bar2style.height = temp;
                }, animationTime * i);
            }
        }
        setTimeout(()=>{
            this.changetoColor3(this.state.arr);
        },animations.length*(animationTime))
    }
    render() {
        const { arr } = this.state;
        return (
            <>
                <button className="btn" onClick={() => {
                    this.resetArray();
                }}>Generate New Array</button>
                <button className="btn" onClick={() => {
                    this.MergeSort();
                }}>MergeSort</button>
                <button className="btn" onClick={() => {
                    this.BubbbleSort();
                }}>BubbbleSort</button>
                <div className="array">
                    {arr.map((value, index) => (
                        <li id={index} style={{ listStyle: "none", height: `${value}px` }}></li>

                    ))}
                </div>


            </>
        );

    }


}
function randomIntegers(min, max) {
    // min and max included 
    return Math.floor(Math.random() * (max - min + 1) + min)
}
export default SortingVisualizer;
